mapper.select:
SELECT n.id, n.title, n.content, n.status, n.priority, n.notice_start_at as noticeStartAt, n.notice_end_at as noticeEndAt, n.view_count as viewCount, n.created_at as createdAt, n.updated_at as updatedAt, n.created_user_id as createdUserId, ac.name as createdUserName, au.name as updatedUserName FROM notice n LEFT JOIN admin ac ON ac.id = n.created_user_id LEFT JOIN admin au ON au.id = n.updated_user_id
AND n.title LIKE CONCAT('%', #{title}, '%')
AND n.content LIKE CONCAT('%', #{content}, '%')
AND n.status = #{status}
AND n.priority = #{priority}
ORDER BY n.created_at DESC
ORDER BY n.created_at ASC
ORDER BY n.view_count DESC, n.created_at DESC
ORDER BY n.notice_start_at DESC,n.created_at DESC
LIMIT #{size} OFFSET #{page}

SELECT COUNT(*) FROM notice n LEFT JOIN admin ac ON ac.id = n.created_user_id LEFT JOIN admin au ON au.id = n.updated_user_id
AND n.title LIKE CONCAT('%', #{title}, '%')
AND n.content LIKE CONCAT('%', #{content}, '%')
AND n.status = #{status}
AND n.priority = #{priority}
