mapper.select:
SELECT q.id, q.title, q.content, q.status, q.file_id as fileId, f.original_name as fileName, q.created_at as createdAt, q.updated_at as updatedAt, q.answered_at as answeredAt, q.answer_content as answerContent, q.answer_user_id as answerUserId, au.name as answerUserName, u.name as userName, f.file_size as fileSize FROM qna q LEFT JOIN file f ON q.file_id = f.id LEFT JOIN user au ON q.answer_user_id = au.id LEFT JOIN user u ON q.user_id = u.id
AND q.user_id = #{userId}
AND q.title LIKE CONCAT('%', #{title}, '%')
AND q.content LIKE CONCAT('%', #{content}, '%')
AND q.status = #{status}
AND q.status != 'CANCELED'
ORDER BY q.created_at ASC
ORDER BY q.created_at DESC
LIMIT #{size} OFFSET #{page}

SELECT COUNT(*) FROM qna q LEFT JOIN file f ON q.file_id = f.id LEFT JOIN user au ON q.answer_user_id = au.id LEFT JOIN user u ON q.user_id = u.id
AND q.user_id = #{userId}
AND q.title LIKE CONCAT('%', #{title}, '%')
AND q.content LIKE CONCAT('%', #{content}, '%')
AND q.status = #{status}
AND q.status != 'CANCELED'
