mapper.select:
SELECT rlr.id, rlr.user_id, rlr.daily_limit, rlr.monthly_limit, rlr.single_limit, rlr.reason, rlr.income_file_id, rlr.bankbook_file_id, rlr.business_file_id, rlr.status, rlr.admin_id, rlr.admin_comment, rlr.processed_at, rlr.created_at, rlr.updated_at, u.name as user_name, income_file.original_name as income_file_name, income_file.file_size as income_file_size, income_file.file_type as income_file_type, bankbook_file.original_name as bankbook_file_name, bankbook_file.file_size as bankbook_file_size, bankbook_file.file_type as bankbook_file_type, business_file.original_name as business_file_name, business_file.file_size as business_file_size, business_file.file_type as business_file_type FROM remittance_limit_request rlr LEFT JOIN user u ON rlr.user_id = u.id LEFT JOIN file income_file ON rlr.income_file_id = income_file.id LEFT JOIN file bankbook_file ON rlr.bankbook_file_id = bankbook_file.id LEFT JOIN file business_file ON rlr.business_file_id = business_file.id
AND rlr.user_id = #{userId}
AND rlr.status = #{status}
AND u.name LIKE CONCAT('%', #{searchTerm}, '%')
ORDER BY
rlr.created_at DESC
rlr.created_at ASC
LIMIT #{size} OFFSET #{page}

SELECT COUNT(*)
FROM remittance_limit_request rlr
LEFT JOIN user u ON rlr.user_id = u.id


        AND rlr.user_id = #{userId}


        AND rlr.status = #{status}


        AND u.name LIKE CONCAT('%', #{searchTerm}, '%')

SELECT COUNT(*)
FROM user_remittance_limit
WHERE user_id = #{userId}
